    grid-template-columns: repeat(3, 1fr);
    grid-gap: 10px;
}

.grid-item {
    background-color: lightgrey;
    padding: 20px;
    text-align: center;
}

Example (Flexbox)

.flex-container {
    display: flex;
    justify-content: space-between;
}

.flex-item {
    background-color: lightgrey;
    padding: 20px;
    text-align: center;
}

